const store= createStore(rootReducer)

store.subscribe(() => {
  saveState({
    modo: store.getState().modo,
    modoTitle: store.getState().modoTitle,
    dataApi: store.getState().modo,
  buyCar: store.getState().modo,
  });
});



export default store
