import React, { Component } from "react";
import Layout from "../../components/layout";
import { connect } from "react-redux";
import SeoHead from "../../components/seoHead";

const mapStateToProps = state => {
  return {
    dataApi: state.dataApi,
  };
};

class ProductConnected extends Component {
  constructor(props){
    super(props);
    this.state = {
      itemName: ""
    }
  }

  componentDidMount() {

  }
  

  componentDidUpdate(){
    console.log(this.props)
    this.props.dataApi.map( item => item.ProductCode === this.props.cc.produc ? this.setState({itemName:item.ProductName}) : null )

    for( let i=0; i < this.props.dataApi.length; i++) {
      if(this.props.dataApi[i].ProductCode === this.props.cc.product ) {
        console.log('adsfsdf')
      }
    }
  }



  render() {
    return (
      <Layout>
        <SeoHead
          title="About"
          description="About description"
          url="ssssssfff"
          ogImage="https://circuit.com.pe/test-nextjs/static/mor.jpg"
        />

        <section className="mainContent">
          <h1>
            {this.state.itemName}
          </h1>
          
        </section>
      </Layout>
    );
  }
}

ProductConnected.getInitialProps = async context => {
  const cc = context.query;
  return { cc };
};

const Product = connect(mapStateToProps)(ProductConnected);

export default Product;
